import React, {useState, useEffect} from 'react';
import axios from 'axios';
import './register.css';
import logo from './covers/shelfLogo.png';


const RegisterPage = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    const handleSubmit = (event) => {
        event.preventDefault();

        // Doğrulama
        if (!username || !password) {
            setErrorMessage('Please fill out all fields.');
            return;
        }

        if (password.length < 6) {
            setErrorMessage('Password must be at least 6 characters long.');
            return;
        }

        // Tüm doğrulama geçtiyse hata mesajını temizle
        setErrorMessage('');
        alert('Form submitted successfully!');
        
        // Burada formu gönderecek kodu ekleyebilirsiniz (örneğin, API'ye veri gönderme).
    };
    


    return (
        <div className = "register-wrapper">
            <div className = "register-header">
            <img src={logo} alt="Logo" className="register-logo" /> 
                <h2>Welcome To ShelfSync</h2>
                <h5>
                    Already have an account? Go to{' '}
                    <button className="text-button">Login Page</button>
                </h5>            </div>
            <div className = "register-form">
                <form>
                    <input type = "text" placeholder = "Username" onChange={(e) => setUsername(e.target.value)} required></input>
                    <input type = "password" placeholder = "Password" onChange={(e) => setPassword(e.target.value)} required></input>
                    <button type = "submit">Register</button>
                    <div id="errorMessage" class="error-message"></div>
                </form>
                
            </div>


        </div>
    )



}


export default RegisterPage;
