import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate
import './profile.css';
import defaultProfilePic from './covers/nidarda.JPG'; // Varsayılan profil fotoğrafı

const ProfilePage = () => {
    const [username, setUsername] = useState('nidoşardoş');
    const [profilePic, setProfilePic] = useState(defaultProfilePic);
    const [bookCount, setBookCount] = useState(100);
    const [currentPassword, setCurrentPassword] = useState('');
    const [newPassword, setNewPassword] = useState('');
    const [passwordError, setPasswordError] = useState('');

    const navigate = useNavigate(); // Initialize useNavigate

    const handleFileChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            const reader = new FileReader();
            reader.onloadend = () => {
                setProfilePic(reader.result);
            };
            reader.readAsDataURL(file);
        }
    };

    const handlePasswordChange = (event) => {
        event.preventDefault();
        if (!currentPassword || !newPassword) {
            setPasswordError('Please fill out all fields.');
            return;
        }
        if (newPassword.length < 6) {
            setPasswordError('New password must be at least 6 characters long.');
            return;
        }

        // Simulate a password change operation
        alert('Password changed successfully!');
        setCurrentPassword('');
        setNewPassword('');
        setPasswordError('');
    };

    const goToHomePage = () => {
        navigate('/main'); // Navigate to the home page
    };

    return (
        <div className="profile-container">
            <div className="profile-header">
                <button className="back-to-home" onClick={goToHomePage}>
                    &larr; Ana Menüye Dön
                </button>
                <img src={profilePic} alt="Profile" className="profile-pic" />
                <h1>{username}</h1>
                <label className="upload-button">
                    <input type="file" accept="image/*" onChange={handleFileChange} />
                    Change Profile Picture
                </label>
            </div>
            <div className="profile-info">
                <h2>Profile Information</h2>
                <div className="profile-info-item">
                    <label>Username:</label>
                    <p>{username}</p>
                </div>
                <div className="profile-info-item">
                    <label>Book Count:</label>
                    <p>{bookCount}</p>
                </div>
                <div className="password-change">
                <h2>Change Password</h2>
                <form onSubmit={handlePasswordChange}>
                    <div className="password-change-item">
                        <label>Current Password:</label>
                        <input
                            type="password"
                            value={currentPassword}
                            onChange={(e) => setCurrentPassword(e.target.value)}
                        />
                    </div>
                    <div className="password-change-item">
                        <label>New Password:</label>
                        <input
                            type="password"
                            value={newPassword}
                            onChange={(e) => setNewPassword(e.target.value)}
                        />
                    </div>
                    {passwordError && <div className="error-message">{passwordError}</div>}
                    <button type="submit">Change Password</button>
                </form>
            </div>
            </div>
            
        </div>
    );
};

export default ProfilePage;
