import React, { useState } from 'react';
import './profile.css';
import defaultProfilePic from './covers/nidarda.JPG'; // Varsayılan profil fotoğrafı

const ProfilePage = () => {
    const [username, setUsername] = useState('nidoşardoş');
    const [email, setEmail] = useState('nidoşardoş@gmail.com');
    const [profilePic, setProfilePic] = useState(defaultProfilePic);
    const [bookCount, setBookCount] = useState(100);

    const handleFileChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            const reader = new FileReader();
            reader.onloadend = () => {
                setProfilePic(reader.result);
            };
            reader.readAsDataURL(file);
        }
    };

    return (
        <div className="profile-container">
            <div className="profile-header">
                <img src={profilePic} alt="Profile" className="profile-pic" />
                <h1>{username}</h1>
                <p>{email}</p>
                <label className="upload-button">
                    <input type="file" accept="image/*" onChange={handleFileChange} />
                    Change Profile Picture
                </label>
            </div>
            <div className="profile-info">
                <h2>Profile Information</h2>
                <div className="profile-info-item">
                    <label>Username:</label>
                    <p>{username}</p>
                </div>
                <div className="profile-info-item">
                    <label>Email:</label>
                    <p>{email}</p>
                </div>
                <div className="profile-info-item">
                    <label>Book Count:</label>
                    <p>{bookCount}</p>
                </div>
            </div>
        </div>
    );
};

export default ProfilePage;
