import React, { useState } from 'react';
import './AddBook.css';

const AddBook = () => {
  const [bookData, setBookData] = useState({
    title: '',
    readDate: '',
    addedDate: '',
    cover: '',
    description: '',
    author: '',
    altAuthor: '',
    publisher: '',
    version: '',
    year: '',
    page: '',
    categories: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setBookData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Verileri txt dosyasına kaydetme
    const bookText = `
      Title: ${bookData.title}
      Read Date: ${bookData.readDate}
      Added Date: ${bookData.addedDate}
      Cover: ${bookData.cover}
      Description: ${bookData.description}
      Author: ${bookData.author}
      Alt Author: ${bookData.altAuthor}
      Publisher: ${bookData.publisher}
      Version: ${bookData.version}
      Year: ${bookData.year}
      Page: ${bookData.page}
      Categories: ${bookData.categories.split(',').join(', ')}
    `;

    // Dosya kaydetme işlemi (simülasyon)
    const blob = new Blob([bookText], { type: 'text/plain' });
    const link = document.createElement('a');
    link.href = URL.createObjectURL(blob);
    link.download = 'bookData.txt';
    link.click();

    // Formu sıfırla
    setBookData({
      title: '',
      readDate: '',
      addedDate: '',
      cover: '',
      description: '',
      author: '',
      altAuthor: '',
      publisher: '',
      version: '',
      year: '',
      page: '',
      categories: ''
    });
  };

  return (
    <div className="add-book-container">
      <h2>Add New Book</h2>
      <form className="add-book-form" onSubmit={handleSubmit}>
        <input type="text" name="title" placeholder="Title" value={bookData.title} onChange={handleChange} required />
        <label> Added Date </label>
        <input type="date" name="readDate" placeholder="Read Date" value={bookData.readDate} onChange={handleChange} required />
        <input type="date" name="addedDate" placeholder="Added Date" value={bookData.addedDate} onChange={handleChange} required />
        <input type="text" name="cover" placeholder="Cover" value={bookData.cover} onChange={handleChange} required />
        <textarea name="description" placeholder="Description" value={bookData.description} onChange={handleChange} required />
        <input type="text" name="author" placeholder="Author" value={bookData.author} onChange={handleChange} required />
        <input type="text" name="altAuthor" placeholder="Alt Author" value={bookData.altAuthor} onChange={handleChange} />
        <input type="text" name="publisher" placeholder="Publisher" value={bookData.publisher} onChange={handleChange} required />
        <input type="text" name="version" placeholder="Version" value={bookData.version} onChange={handleChange} required />
        <input type="text" name="year" placeholder="Year" value={bookData.year} onChange={handleChange} required />
        <input type="text" name="page" placeholder="Page Count" value={bookData.page} onChange={handleChange} required />
        <input type="text" name="categories" placeholder="Categories (comma-separated)" value={bookData.categories} onChange={handleChange} required />
        <button type="submit">Save Book</button>
      </form>
    </div>
  );
};

export default AddBook;
