import React, { useState } from 'react';
import BookDetails from './BookDetails';
// import './BookStyles.css';

const BookListView = ({ book}) => {

  const [selectedBook, setSelectedBook] = useState(null);
  const [visibleColumns, setVisibleColumns] = useState({
    title: true,
    author: true,
    altAuthor: true,
    publisher: true,
    year: true,
    page: true,
  });

  const handleBookClick = (book) => {
    setSelectedBook(book);
  };

  const closeDetails = () => {
    setSelectedBook(null);
  };

  const toggleColumn = (column) => {
    setVisibleColumns(prevState => ({
      ...prevState,
      [column]: !prevState[column]
    }));
  };

  return (
    <div className="book-table-container">
      <div className="filter-buttons">
        <button onClick={() => toggleColumn('title')}>Kitap Adı</button>
        <button onClick={() => toggleColumn('author')}>Yazar</button>
        <button onClick={() => toggleColumn('publisher')}>Yayınevi</button>
        <button onClick={() => toggleColumn('year')}>Basım Yılı</button>
        <button onClick={() => toggleColumn('page')}>Sayfa Sayısı</button>
      </div>

      <div className="book-table">
        <div className="book-table-header">
          {visibleColumns.title && <div className="book-table-column">Kitap Adı</div>}
          {visibleColumns.author && <div className="book-table-column">Yazar</div>}
          {visibleColumns.altAuthor && <div className="book-table-column">Alt Yazar</div>}
          {visibleColumns.publisher && <div className="book-table-column">Yayınevi</div>}
          {visibleColumns.year && <div className="book-table-column">Basım Yılı</div>}
          {visibleColumns.page && <div className="book-table-column">Sayfa Sayısı</div>}
        </div>
        {books.map((book) => (
          <div key={book.id} className="book-table-row" onClick={() => handleBookClick(book)}>
            {visibleColumns.title && <div className="book-table-column">{book.title}</div>}
            {visibleColumns.author && <div className="book-table-column">{book.author}</div>}
            {visibleColumns.altAuthor && <div className="book-table-column">{book.altAuthor}</div>}
            {visibleColumns.publisher && <div className="book-table-column">{book.publisher}</div>}
            {visibleColumns.year && <div className="book-table-column">{book.year}</div>}
            {visibleColumns.page && <div className="book-table-column">{book.page}</div>}
          </div>
        ))}
      </div>
      {selectedBook && <BookDetails book={selectedBook} closeDetails={closeDetails} />}
    </div>
  );
};

export default BookListView;
