import React from 'react';
import './profile.css';
import defaultProfilePic from './covers/nidarda.JPG'; // Varsayılan profil fotoğrafı

const ProfilePage = () => {
    const user = {
        username: 'nidoşardoş',
        email: 'nidoşardoş@gmail.com',
        profilePic: defaultProfilePic, // Kullanıcının profil fotoğrafı
        bookcount : 100
    };

    return (
        <div className="profile-container">
            <div className="profile-header">
                <img src={user.profilePic} alt="Profile" className="profile-pic" />
                <h1>{user.username}</h1>
                <p>{user.email}</p>
            </div>
            <div className="profile-info">
                <h2>Profile Information</h2>
                <div className="profile-info-item">
                    <label>Username:</label>
                    <p>{user.username}</p>
                </div>
                <div className="profile-info-item">
                    <label>Email:</label>
                    <p>{user.email}</p>
                </div>
            </div>
        </div>
    );
};

export default ProfilePage;
