import React from 'react';

const BookDetails = ({ book, closeDetails }) => {
  return (
    <div className="book-details">
      <div className="details-content">
        <span className="close" onClick={closeDetails}>&times;</span>
        <div>
        <img className = "book-image" src="https://marketplace.canva.com/EAFaQMYuZbo/1/0/1003w/canva-brown-rusty-mystery-novel-book-cover-hG1QhA7BiBU.jpg"></img>
        <div className = "book-info">
          <h2>{book.title}</h2>
          <p>{book.author }</p>
          <p>{book.altAuthor }</p>
          <p>{book.aublisher }</p>
          <p>{book.version }</p>
          <p>{book.year }</p>
          <p>{book.page  }</p>
        </div>
        
        <p>{book.coverType  }</p>
      </div>
      <p>{book.description   }</p>
      </div>
      
    </div>
  );
};

export default BookDetails;
