import React, { useState } from 'react';
import BookDetails from './BookDetails';
import './BookList.css';

const BookList = () => {
  const books = [
    {
      id: 1,
      title: 'Kitap 1',
      cover: 'book1-cover.jpg',
      description: 'Kitap 1 hakkında detaylı bilgi burada yer alacak.',
    },
    {
      id: 2,
      title: 'Kitap 2',
      cover: 'book2-cover.jpg',
      description: 'Kitap 2 hakkında detaylı bilgi burada yer alacak.',
    },
    // Diğer kitaplar eklenebilir
  ];

  const [selectedBook, setSelectedBook] = useState(null);

  const handleBookClick = (book) => {
    setSelectedBook(book);
  };

  const closeDetails = () => {
    setSelectedBook(null);
  };

  return (
    <div className="book-list">
      {books.map((book) => (
        <div key={book.id} className="book" onClick={() => handleBookClick(book)}>
          <img src={book.cover} alt={book.title} />
          <h3>{book.title}</h3>
        </div>
      ))}
      {selectedBook && <BookDetails book={selectedBook} closeDetails={closeDetails} />}
    </div>
  );
};

export default BookList;
