import React, { useState } from 'react';
import './register.css';
import logo from './covers/shelfLogo.png';

const RegisterPage = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    const handleSubmit = (event) => {
        event.preventDefault();

        // Doğrulama
        if (!username || !password) {
            setErrorMessage('Please fill out all fields.');
            return;
        }

        if (password.length < 6) {
            setErrorMessage('Password must be at least 6 characters long.');
            return;
        }

        // Tüm doğrulama geçtiyse hata mesajını temizle
        setErrorMessage('');
        alert('Form submitted successfully!');
        
        // Burada formu gönderecek kodu ekleyebilirsiniz (örneğin, API'ye veri gönderme).
    };

    return (
        <div className="register-wrapper">
            <div className="register-header">
                <img src={logo} alt="Logo" className="register-logo" />
                <h2>Welcome To ShelfSync</h2>
                <h5>
                    Already have an account? Go to{' '}
                    <button className="text-button">Login Page</button>
                </h5>
            </div>
            <div className="register-form">
                <form onSubmit={handleSubmit}>
                    <input
                        type="text"
                        placeholder="Username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        
                    />
                    <input
                        type="password"
                        placeholder="Password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        
                    />
                    <button type="submit">Register</button>
                    {errorMessage && <div id="errorMessage" className="error-message">{errorMessage}</div>}
                </form>
            </div>
        </div>
    );
};

export default RegisterPage;
