import React, { useState } from 'react';
import './register.css';
import logo from './covers/shelfLogo.png';
import axios from 'axios';

const RegisterPage = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    const headers = {
        'Content-Type': 'application/json',
      }

    const handleLoginSubmit = (event) => {
        event.preventDefault();

        if (!username || !password) {
            setErrorMessage('Please fill out all fields.');
            return;
        }

        if (password.length < 6) {
            setErrorMessage('Password must be at least 6 characters long.');
            return;
        }

        

        // Tüm doğrulama geçtiyse hata mesajını temizle
        setErrorMessage('');
        alert('Form submitted successfully!');
        
       axios.post('https://', {username, password}, {
        headers: headers
      })
    };

    return (
        <div>
            <div>
            <img src={logo} alt="Logo" className="register-logo" />
            <h2>Welcome To ShelfSync</h2>
            </div>
        <div className="register-wrapper">
            <div className="register-header">
                
                <h5>
                   Register
                </h5>
            </div>
            <div className="register-form">
                <form onSubmit={handleLoginSubmit}>
                    <input
                        type="text"
                        placeholder="Username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        
                    />
                    <input
                        type="password"
                        placeholder="Password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        
                    />
                    <button type="submit">Register</button>
                    {errorMessage && <div id="errorMessage" className="error-message">{errorMessage}</div>}
                </form>
            </div>
            
        </div>

        <div className="register-wrapper">
            <div className="register-header">
                
                <h5>
                   Login
                </h5>
            </div>
            <div className="register-form">
                <form onSubmit={handleLoginSubmit}>
                    <input
                        type="text"
                        placeholder="Username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        
                    />
                    <input
                        type="password"
                        placeholder="Password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        
                    />
                    <input
                        type="password"
                        placeholder="Confirm Password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        
                    />
                    <button type="submit">Register</button>
                    {errorMessage && <div id="errorMessage" className="error-message">{errorMessage}</div>}
                </form>
            </div>
            
        </div>
        </div>
    );
};

export default RegisterPage;
